package com.sysntax.class05;

public class Recap {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//shortcut to autocomplete
	// "if else if " we call it as ladder, because it check the condition step by step like ladder.
		String phone="Samsung";
		
		if(phone.equals("iphone")) {
			
			System.out.println("i want to by it for me");
		}else if(phone.equals("Samsung")) {
			
			System.out.println("i want to by it for my husband");
		}else if(phone.equals("nokia")) {
			
			System.out.println("i want to by it for grandma");
		}else {}
		
		System.out.println("this is not phone i was looking for");
	}

}
