package com.sysntax.class07;

public class HomeWork01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//Print numbers from 1 to 100 in 1 line with space
		
	
		for(int i=1 ; i<=100 ; i++) {
			
			System.out.print(i + " ");
		}
		System.out.println();
		System.out.println("******************************* while loop***********************************************************");
	
		int i=1;
		 
		while(i<=100) {
			System.out.print(i + " ");
			i++;
			
		}
	
	}
	

}

